class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int m = grid.size();
        int n = grid[0].size();
        int ans = 0;
        for(int i = 0; i < m/2; i++){
            for(int j = 0; j < n/2; j++){
                int u = 0, v = 0;
                if(grid[i][j]) v++;
                else u++;
                if(grid[i][n-1-j]) v++;
                else u++;
                if(grid[m-1-i][j]) v++;
                else u++;
                if(grid[m-1-i][n-1-j]) v++;
                else u++;

                if(u==4 || u==0){
                    ans = ans;
                }else if(u==3){
                    ans = ans + 1;
                }else if(u==2){
                    ans = ans+2;
                }else if(u==1){
                    ans = ans+1;
                }
            }
        }

        int cnt1 = 0,cnt0 = 0;
        if(m&1){
            int i = m/2;
            for(int j = 0; j < n/2; j++){
                if(grid[i][j]!=grid[i][n-1-j]){
                    cnt0++;
                }else{
                    if(grid[i][j]==1) cnt1++;
                }
            }
        }
        // cout<<cnt0<<" "<<cnt1<<endl;

        if(n&1){
            int j = n/2;
            for(int i = 0; i < m/2; i++){
                if(grid[i][j]!=grid[m-1-i][j]){
                    cnt0++;
                }else{
                    if(grid[i][j]==1) cnt1++;
                }
            }
        }

        // cout<<ans<<endl;
        // cout<<cnt0<<" "<<cnt1<<endl;
        cnt1 *= 2;
        int rem = cnt1%4;

        if(rem==2){
            if(cnt0>=rem/2){
                ans += 1;
                cnt0 -= 1;
                rem = 0;
            }else{
                if(cnt0==0){
                    ans += 2;
                    rem = 0;
                }
            }
        }

        if(rem==0){
            ans += cnt0;
        }else{
            ans += 2;
        }
        if((m&1) && (n&1) && grid[m/2][n/2] ) ans++;

        return ans;


    }
};
