class Solution
{
public:
    int ans = 0;
    int minFlips(vector<vector<int>> &grid)
    {
          vector<int> s(3, 0);
        int n = grid.size(), m = grid[0].size(), ans = 0;

        if (n % 2 == 1 && m % 2 == 1)
        {
            if (grid[n / 2][m / 2] == 1)
            {
                ans += 1;
            }
        }


        if (m % 2 == 1)
        {
            int a = 0, b = n - 1;
            while (a < b)
            {
                int x = grid[a][m / 2] + grid[b][m / 2];
                s[x] += 1;
                a += 1, b -= 1;
            }
        }
        if (n % 2 == 1)
        {
            int a = 0, b = m - 1;
            while (a < b)
            {
                int x = grid[n / 2][a] + grid[n / 2][b];
                s[x] += 1;
                a += 1, b -= 1;
            }
        }

        if (s[2] % 2 == 1)
        {
            if (s[1] > 0)
            {
                ans += s[1];
            }
            else
            {
                ans += 2;
            }
        }
        else
        {
            ans += s[1];
        }

        for (int i = 0; i < n / 2; ++i)
        {
            for (int j = 0; j < m / 2; ++j)
            {
                int x = grid[i][j] +  grid[n - 1 - i][j] + grid[i][m - 1 - j] + grid[n - 1 - i][m - 1 - j];

                if (x != 0 && x != 4)
                {
                    ans += min(x, 4 - x);
                }
            }
        }

        return ans;
    }
};
