class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int n=grid.size(),m=grid[0].size();
        int ans=0,z=0;
        bool flag=false;
        for(int l1=0,r1=n-1;l1<=r1;++l1,--r1)
            for(int l2=0,r2=m-1;l2<=r2;++l2,--r2)
            if(l1!=r1&&l2!=r2){
                vector<int>s(2);
                ++s[grid[l1][l2]];
                ++s[grid[l1][r2]];
                ++s[grid[r1][l2]];
                ++s[grid[r1][r2]];
                ans+=min(s[0],s[1]);
            }
            else if(l1==r1&&l2==r2){
                ans+=grid[l1][l2];
            }
            else if(l1==r1){
                if(grid[l1][l2]==grid[l1][r2])
                    z+=grid[l1][l2]+grid[l1][r2];
                else{
                    ++ans;
                    flag=true;
                }
            }
            else{
                if(grid[l1][l2]==grid[r1][l2])
                {
                    z+=grid[l1][l2]+grid[r1][l2];
                }
                else{
                    ++ans;
                    flag=true;
                }
            }
        if(!flag&&z%4==2) ans+=2;
        return ans;
    }
};
