class Solution {
public:
    int minFlips(vector<vector<int>>& a) {
        int n = a.size(), m = a[0].size();
        int mn = 0;
        for (int r=0;r<n/2;r++)
        {
            for (int c=0;c<m/2;c++)
            {
                int cnt = a[r][c] + a[n-1-r][c] + a[r][m-1-c] + a[n-1-r][m-1-c];
                if (cnt == 1 || cnt == 3)
                    mn++;
                else if (cnt == 2)
                    mn+=2;
            }
        }
        int cnt = 0, bad = 0;
        if (m%2 == 1)
        {
            for (int i=0;i<n/2;i++)
            {
                if (a[i][m/2] != a[n-i-1][m/2])
                    bad++;
                else
                   cnt += a[i][m/2] + a[n-i-1][m/2];
            }
        }
        if (n%2 == 1)
        {
            for (int j=0;j<m/2;j++)
            {
                if (a[n/2][j] != a[n/2][m-1-j])
                    bad++;
                else
                    cnt += a[n/2][j] + a[n/2][m-1-j];
            }
        }
        if (n%2 == 1 && m%2 == 1 && a[n/2][m/2] == 1)
            mn++;
        //cout<<mn<<" "<<cnt<<" "<<bad<<"\n";
        int ans = INT_MAX;
        for (int i=0;i<=n+m;i+=4)
        {
            int cur = mn;
            if (i <= cnt)
                cur += cnt - i + bad;
            else
            {
                int req = i - cnt;
                if (req <= 2*bad)
                    cur += bad;
                else
                {
                    cur += bad + 2*(req - 2*bad);
                }
            }
            ans = min(ans, cur);
        }
        return ans;
    }
};
