class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int result = 0;
        int row = grid.size();
        int col = grid[0].size();

        if (row % 2 == 1 && col % 2 == 1) {
            int aaa = row / 2;
            int bbb = col / 2;
            if (grid[aaa][bbb] == 1) result += 1;
        }

        vector<int> shift(3, 0);

        if (row % 2 == 1) {
            int a = 0, b = col - 1;
            while (a < b) {
                int sm = grid[row / 2][a] + grid[row / 2][b];
                shift[sm] += 1;
                a += 1;
                b -= 1;
            }
        }

        if (col % 2 == 1) {
            int a = 0;
            int b = row - 1;
            while (a < b) {
                int sm = grid[a][col / 2] + grid[b][col / 2];
                shift[sm] += 1;
                a += 1;
                b -= 1;
            }
        }

        if (shift[2] % 2 == 1) {
            if (shift[1] > 0) {
                result += shift[1];
            } else {
                result += 2;
            }
        } else {
            result += shift[1];
        }


        for (int i = 0; i < row / 2; ++i) {
            for (int j = 0; j < col / 2; ++j) {
                int a = grid[i][j];
                int b = grid[row - 1 - i][j];
                int c = grid[i][col - 1 - j];
                int d = grid[row - 1 - i][col - 1 - j];

                int summ = a + b + c + d;

                if (summ != 0 && summ != 4) {
                    result += min(summ, 4 - summ);
                }
            }
        }

        return result;
    }
};
