class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int numRows = grid.size();
        int numCols = grid[0].size();
        int flipCount = 0;

        if (numRows % 2 == 1 && numCols % 2 == 1) {
            if (grid[numRows / 2][numCols / 2] == 1) {
                flipCount += 1;
            }
        }

        vector<int> edgeFlips(3, 0);
        if (numRows % 2 == 1) {
            int left = 0, right = numCols - 1;
            while (left < right) {
                int edgeSum = grid[numRows / 2][left] + grid[numRows / 2][right];
                edgeFlips[edgeSum] += 1;
                left += 1;
                right -= 1;
            }
        }
        if (numCols % 2 == 1) {
            int top = 0, bottom = numRows - 1;
            while (top < bottom) {
                int edgeSum = grid[top][numCols / 2] + grid[bottom][numCols / 2];
                edgeFlips[edgeSum] += 1;
                top += 1;
                bottom -= 1;
            }
        }
        if (edgeFlips[2] % 2 == 1) {
            if (edgeFlips[1] > 0) {
                flipCount += edgeFlips[1];
            } else {
                flipCount += 2;
            }
        } else {
            flipCount += edgeFlips[1];
        }

        for (int row = 0; row < numRows / 2; ++row) {
            for (int col = 0; col < numCols / 2; ++col) {
                int topLeft = grid[row][col];
                int bottomLeft = grid[numRows - 1 - row][col];
                int topRight = grid[row][numCols - 1 - col];
                int bottomRight = grid[numRows - 1 - row][numCols - 1 - col];

                int cornerSum = topLeft + bottomLeft + topRight + bottomRight;

                if (cornerSum != 0 && cornerSum != 4) {
                    flipCount += min(cornerSum, 4 - cornerSum);
                }
            }
        }

        return flipCount;
    }
};
