class Solution:
    def minFlips(self, grid: List[List[int]]) -> int:

        n=len(grid)
        m=len(grid[0])
        ans=0
        c01=0
        c11=0
        for i in range((n+1)//2):
            for j in range((m+1)//2):
                cnt=[0,0]
                s=1
                cnt[grid[i][j]]+=1
                if i!=n-i-1:
                    s+=1
                    cnt[grid[n-i-1][j]]+=1
                    if j!=m-j-1:
                        s+=1
                        cnt[grid[n-i-1][m-j-1]]+=1
                if j!=m-j-1:
                    s+=1
                    cnt[grid[i][m-j-1]]+=1
                if s==1:
                    ans+=s-cnt[0]
                elif s==2:
                        if cnt[0]==1:
                            c01+=1
                        elif cnt[1]==2:
                            c11+=1
                elif s==4:
                    ans+=s-max(cnt)
        # print(c11,c01)
        c11%=2
        if c11>0 and c01==0:
            ans+=2
        else:
            ans+=c01
        return ans
