class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int m = grid.size(), n = grid[0].size();

        int cnt1 = 0, cnt21 = 0, cnt22 = 0, cnt3 = 0;
        for(int i = 0; i <= (m - 1) / 2; ++i) {
            for(int j = 0; j <= (n - 1) / 2; ++j) {
                int sym_i = m - 1 - i;
                int sym_j = n - 1 - j;
                int num1 = grid[i][j];
                int num2 = grid[i][sym_j];
                int num3 = grid[sym_i][j];
                int num4 = grid[sym_i][sym_j];
                // cout << i << sym_i << j << sym_j << endl;
                // cout << num1 << num2 << num3 << num4 << endl;
                if(i != sym_i && j != sym_j) { // 0 or 4
                    // 4 nums, sum : 0 1 2 3 4
                    int sum = num1 + num2 + num3 + num4;
                    // cout << sum << endl;
                    cnt1 += min(sum, 4 - sum);
                } else if (i == sym_i && j == sym_j) { // 0 or 1
                    // 1 num, sum : 0 1
                    int sum = num1;
                    // cout << sum << endl;
                    cnt3 += sum;
                } else { // i == sym_i || j == sym_j, 0 or 2
                    // 2 nums, sum : 0 1 2
                    int sum = num1 + (i == sym_i ? num2 : num3);
                    // cout << sum << endl;
                    if(sum == 1) {
                        cnt21 += sum;
                    } else if (sum == 2) {
                        cnt22 += sum;
                        // cout << cnt22 << endl;
                    } // else sum == 0
                }
            }
        }
        // cout << cnt1 << cnt21 << cnt22 << cnt3 << endl;

        int res = cnt1 + cnt21 + cnt3;
        if(cnt22 % 4 != 0) { // cnt22 % 4 == 0 or 2
            if(cnt22 % 4 > cnt21 * 2) {
                res += cnt22 % 4; // 2
            }
        }

        return res;
    }
};
