class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int c=0;
        int n=grid.size();
        int m=grid[0].size();
        vector<int> arr(100);
        if(m&1){
            int s=0,e=n-1;
            while(s<e){
                arr[grid[s][m/2]+grid[e][m/2]]++;
                s++;
                e--;
            }
        }
        if(n&1){
            int s=0,e=m-1;
            while(s<e){
                arr[grid[n/2][s]+grid[n/2][e]]++;
                s++;
                e--;
            }
        }
        int p=arr[2],q=arr[1];
        if(p&1){
            c+=(q<=0?2:q);
        }
        else c+=q;
        for(int i=0;i<n/2;i++){
            for(int j=0;j<m/2;j++){
                int su=grid[i][j]+grid[i][m-1-j]+grid[n-1-i][j]+grid[n-1-i][m-1-j];
                if(su==0||su==4)continue;
                c+=min(su,4-su);
            }
        }
        if((n&1)&&(m&1)){
            if(grid[n/2][m/2]==1)c++;
        }
        return c;
    }
};
