class Solution {
    public int minFlips(int[][] grid) {
        int r=grid.length;
        int c=grid[0].length;
        int count =0;
        boolean canChange = false;
        int onesCount = 0;
        int n1=0,n2=0,n3=0,n4=0;
        for(int i=0;i<=(r-1)/2;i++){
            for(int j=0;j<=(c-1)/2;j++){
                // System.out.println("i: "+i+" j: "+j);
                // Exact Mid
                if(c%2==1 && j==c/2 && r%2==1 && i==r/2){
                    if(grid[i][j]==1) count+=1;
                    continue;
                }
                n1=grid[i][j];
                n2=grid[i][c-1-j];
                n3=grid[r-1-i][j];
                n4=grid[r-1-i][c-1-j];

                // middle row
                if(r%2==1 && i==r/2){

                    if(n1==n2 && n1==1) onesCount+=2;
                    else if(n1!=n2){
                        count+=1;
                        canChange=true;
                    }
                    continue;
                }


                // middle col
                if(c%2==1 && j==c/2){

                    if(n1==n3 && n3==1) onesCount+=2;
                    else if(n3!=n1){
                        count+=1;
                        canChange=true;
                    }
                    continue;
                }


                int[] res=getCostForOperation(n1,n2,n3,n4);
                // System.out.println("Resposne from method: "+Arrays.toString(res));
                if(res[0]==0){
                    onesCount+=4;
                }
                count+=res[1];
            }
        }
        // System.out.println("Ones: "+onesCount+"   canChange: "+canChange);
        if(onesCount%4!=0){
            if(!canChange) count+=2;
        }
        return count;
    }
    public int[] getCostForOperation(int n1,int n2,int n3,int n4){
        int zeroes=0;
        int ones=0;
        if(n1==0){
            zeroes+=1;
        }else ones+=1;
        if(n2==0) zeroes+=1;
        else ones+=1;
        if(n3==0) zeroes+=1;
        else ones+=1;
        if(n4==0)zeroes+=1;
        else ones+=1;
        if(zeroes>ones){
            return new int[]{1,ones};
        }
        else if(ones>zeroes){
            return new int[]{0,zeroes};
        }
        return new int[]{1,ones};
    }
}
