class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int n = grid.size();
        int m = grid[0].size();
        int ans = 0;
        for (int i=0;n-1-i>i;i++) {
            for (int j=0;m-1-j>j;j++) {
                int cnt = 0;
                if (grid[i][j]) cnt++;
                if (grid[i][m-1-j]) cnt++;
                if (grid[n-1-i][j]) cnt++;
                if (grid[n-1-i][m-1-j]) cnt++;
                ans += min(cnt, 4-cnt);
            }
        }
        if (n % 2 && m % 2 && grid[n/2][m/2]) ans++;
        int cnt1 = 0;
        int cnt2 = 0;
        if (n % 2) {
            for (int j=0;m-1-j>j;j++) {
                if (grid[n/2][j] != grid[n/2][m-1-j]) {
                    cnt1++;
                    grid[n/2][j] = 0;
                    grid[n/2][m-1-j] = 0;
                }
                if (grid[n/2][j]) cnt2++;
                if (grid[n/2][m-1-j]) cnt2++;
            }
        }
        if (m % 2) {
            for (int i=0;n-1-i>i;i++) {
                if (grid[i][m/2] != grid[n-1-i][m/2]) {
                    cnt1++;
                    grid[i][m/2] = 0;
                    grid[n-1-i][m/2] = 0;
                }
                if (grid[i][m/2]) cnt2++;
                if (grid[n-1-i][m/2]) cnt2++;
            }
        }
        if (cnt2 % 4 == 2 && !cnt1) ans += 2;
        return ans + cnt1;
    }
};
