class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int s = grid.size();
        int m = grid[0].size();
        int mid1 = ceil(s/2),mid2 = ceil(m/2);
        int count1=0;
        for(auto i:grid){
            for(auto j:i){
                if(j==1)count1++;
            }
        }
        int ans=0;
        vector<int> a1(3,0);
        for(int i=0;i<s/2;i++){
            for(int j=0;j<m/2;j++){
                int c0=0;
                int c1=0;
                if(grid[i][j]==0)c0++;
                else c1++;
                if(grid[s-i-1][j]==0)c0++;
                else c1++;
                if(grid[i][m-j-1]==0)c0++;
                else c1++;
                if(grid[s-i-1][m-j-1]==0)c0++;
                else c1++;
                ans+=min(c0,c1);
            }
        }
        int count_2_ones =0;
        int count_1_ones =0;
        if(s%2!=0 && m%2!=0 && grid[mid1][mid2]==1)ans++;
        if(s%2!=0){
            int i = ceil(s/2);
            for(int j=0;j<m/2;j++){
                if(grid[i][j]!=grid[i][m-j-1]){ans++;count_1_ones++;}
                else{
                    if(grid[i][j]==1)count_2_ones++;
                }
            }
        }
        if(m%2!=0){
            int j = ceil(m/2);
            for(int i=0;i<s/2;i++){
                if(grid[i][j]!=grid[s-i-1][j]){ans++;count_1_ones++;}
                else{
                    if(grid[i][j]==1)count_2_ones++;
                }
            }
        }

        if(count_2_ones%2==1){
            if(count_1_ones==0)ans+=2;
            else if(count_1_ones==0)ans+=1;
        }
        return ans;
    }
};
