class Solution {
public:
    int minFlips(vector<vector<int>>& matrix) {
         int rows = matrix.size();
        int cols = matrix[0].size();
        int totalFlips = 0;

        // Check if both dimensions are odd and if the center cell needs a flip
        if (rows % 2 == 1 && cols % 2 == 1) {
            if (matrix[rows / 2][cols / 2] == 1) {
                totalFlips += 1;
            }
        }

        vector<int> flipCounts(3, 0);
        // If the number of rows is odd, check the middle row
        if (rows % 2 == 1) {
            int left = 0, right = cols - 1;
            while (left < right) {
                int sum = matrix[rows / 2][left] + matrix[rows / 2][right];
                flipCounts[sum] += 1;
                left++;
                right--;
            }
        }

        // If the number of columns is odd, check the middle column
        if (cols % 2 == 1) {
            int top = 0, bottom = rows - 1;
            while (top < bottom) {
                int sum = matrix[top][cols / 2] + matrix[bottom][cols / 2];
                flipCounts[sum] += 1;
                top++;
                bottom--;
            }
        }

        // Adjust the total flips based on the flipCounts
        if (flipCounts[2] % 2 == 1) {
            if (flipCounts[1] > 0) {
                totalFlips += flipCounts[1];
            } else {
                totalFlips += 2;
            }
        } else {
            totalFlips += flipCounts[1];
        }

        // Calculate minimum flips for each 2x2 block
        for (int i = 0; i < rows / 2; ++i) {
            for (int j = 0; j < cols / 2; ++j) {
                int cell1 = matrix[i][j];
                int cell2 = matrix[rows - 1 - i][j];
                int cell3 = matrix[i][cols - 1 - j];
                int cell4 = matrix[rows - 1 - i][cols - 1 - j];

                int blockSum = cell1 + cell2 + cell3 + cell4;

                if (blockSum != 0 && blockSum != 4) {
                    totalFlips += min(blockSum, 4 - blockSum);
                }
            }
        }

        return totalFlips;
    }
};
