class Solution {
public:
    int f(const vector<vector<int>>& grid, int n, int m,vector<int>&arr) {
    int ans = 0;
    if (n % 2 == 1 && m % 2 == 1) {
        if (grid[n / 2][m / 2] == 1) {
            ans += 1;
        }
    }

    if (n % 2 == 1) {
        int x= 0;
        int y = m - 1;
        while (x < y) {
            int p = grid[n / 2][x] + grid[n / 2][y];
            arr[p] += 1;
            x++;y--;
        }
    }

    if (m % 2 == 1) {
        int x = 0;
        int y= n - 1;
        while (x < y) {
            int p = grid[x][m / 2] + grid[y][m / 2];
            arr[p] += 1;
             x++;y--;
        }
    }

    if (arr[2] % 2 == 1) {
        if (arr[1] > 0) {
            ans += arr[1];
        } else   ans += 2;

    } else   ans += arr[1];


    for (int i = 0; i < n / 2; ++i) {
        for (int j = 0; j < m / 2; ++j) {
            int x = grid[i][j];
            int y = grid[n - 1 - i][j];
            int z = grid[i][m - 1 - j];
            int w = grid[n - 1 - i][m - 1 - j];

            int p = x+ y + z + w;

            if (p != 0 && p != 4) {
                ans += min(p, 4 - p);
            }
        }
    }

    return ans;
}
    int minFlips(vector<vector<int>>& grid) {
       int n = grid.size();
       int m = grid[0].size();
       vector<int>arr(3,0);
    return f(grid, n, m,arr);
    }
};
