class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
         int n=grid.size();
         int m =grid[0].size();
         int ans=0;
        if(n&1 and m&1){
            if(grid[n/2][m/2]==1){
                ans++;
            }
        }
        int size=4;
        vector<int> temp(size-1,0);
        if(n&1){
            int p1=0;
            int p2=m-1;
            while(p1<p2){
                int sum=grid[n/2][p1] + grid[n/2][p2];
                temp[sum] +=1;
                p1++;
                p2--;
            }
        }
        if(m&1){
            int left=0;
            int right=n-1;
            while(left<right){
                int sum=grid[left][m/2] + grid[right][m/2];
                temp[sum]+=1;
                left++;
                right--;
            }
        }
        if(temp[2]&1){
             if(temp[1]>0){
                 ans+=temp[1];
             }else{
                 ans+=2;
             }
        }else{
            ans+=temp[1];
        }
        for(int i=0;i<n/2;i++){
            for(int j=0;j<m/2;j++){
                int p1=grid[i][j];
                int p2=grid[n-1-i][j];
                int p3=grid[i][m-1-j];
                int p4=grid[n-1-i][m-1-j];
                int ans2=p1+p2+p3+p4;
                if(ans2!=0   and ans2!=4){
                    ans+=min(ans2,4-ans2);
                }
            }
        }
        return ans;
    }
};
