class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int ans = 0 ,n =grid.size(),m = grid[0].size();

        int starti = 0;
        int endi = n - 1;
        while(starti < endi){
            int startj = 0;
            int endj = m - 1;
            while(startj < endj){
                int ones = grid[starti][startj] + grid[starti][endj] + grid[endi][startj] + grid[endi][endj];
                int zeros = 4 - ones;
                if(ones == 2 ) ans+=2;
                else if(ones == 3 || ones == 1) ans+=1;

                startj++;
                endj--;
            }
            starti++;
            endi--;
        }

        if(n%2==0 && m%2==0) return ans;

         // cout<<ans<<endl;
        int flips = 0;
        int double_one = 0;

        if(n % 2!=0 && m %2!=0){
            int start = 0;
            int end = m-1;
            while(start < end){
                if(grid[n/2][start] != grid[n/2][end]) flips++;
                else if(grid[n/2][start] == 1) double_one+=2;
                start++;
                end--;
            }

            start = 0;
            end = n-1;
            while(start < end){
                if(grid[start][m/2] != grid[end][m/2]) flips++;
                else if(grid[start][m/2] == 1) double_one+=2;
                start++;
                end--;
            }
            if(double_one  % 4!=0){
                if(flips == 0) ans+=2;
                else ans+=flips;
            }else{
                 ans+=flips;
            } 
        }else if(n % 2 !=0 ){
            int start = 0;
            int end = m-1;
            while(start < end){
                if(grid[n/2][start] != grid[n/2][end]) flips++;
                else if(grid[n/2][start] == 1) double_one+=2;
                start++;
                end--;
            }

            if(double_one  % 4!=0){
                if(flips == 0) ans+=2;
                else ans+=flips;
            }else{
                 ans+=flips;
            }
        }
        else if(m % 2 !=0 ){
            int start = 0;
            int end = n-1;
            while(start < end){
                if(grid[start][m/2] != grid[end][m/2]) flips++;
                else if(grid[start][m/2] == 1) double_one+=2;
                start++;
                end--;
            }

            if(double_one  % 4!=0){
                if(flips == 0) ans+=2;
                else ans+=flips;
            }else{
                 ans+=flips;
            }
        }

        if(n % 2 !=0 && m % 2 !=0){
            ans+=grid[n/2][m/2];
        }

        return ans;



    }
};
