class Solution {
public:
    int minFlips(vector<vector<int>>& A) {
        int n=A.size(), m=A[0].size();
        int res=0;
        if(n%2 and m%2){
            if(A[n/2][m/2]) res++;
        }
        int z=0, o=0, t=0;
        if(n%2){
            int s=0, e=m-1;
            while(s<e){
                int tp=A[n/2][s++]+A[n/2][e--];
                if(tp==0) z++;
                else if(tp==1) o++;
                else t++;
            }
        }
        if(m%2){
            int s=0, e=n-1;
            while(s<e){
                int tp=A[s++][m/2]+A[e--][m/2];
                if(tp==0) z++;
                else if(tp==1) o++;
                else t++;
            }
        }
        if(t%2){
            if(o) res+=o;
            else res+=2;
        } else res += o;
        for(int i=0;i<n/2;i++){
            for(int j=0;j<m/2;j++){
                int p=0;
                p += A[i][j];
                p += A[n-i-1][j];
                p += A[n-i-1][m-j-1];
                p += A[i][m-j-1];
                if(p%4) res += min(p, 4-p);
            }
        }
        return res;
    }
};
