class Solution {
public:
    int minFlips(vector<vector<int>>& grid) 
    {
        int n = grid.size();
        int m = grid[0].size();

        auto f = [&](int x, int y)
        {
            if(x >= 0 && x < n && y >= 0 && y < m) return true;
            return false;
        };

        int ini = 0;
        for(int i = 0; i < n; i++)
        {
            for(int j = 0; j < m; j++) 
            {
                if(grid[i][j] == 1) ini++;
            }
        }

        int ans = 0;
        int one = 0, two = 0;
        vector<vector<int>> a = grid;
        int fix0 = false, fix1 = false;
        for(int i = 0; i < n; i++)
        {
            for(int j = 0; j < m; j++)
            {
                set<pair<int, int>> st;
                // i, j
                if(f(i, j)) st.insert({i, j});
                // n - i - 1, j
                if(f(n - i - 1, j)) st.insert({n - i - 1, j});
                // i, m - j - 1
                if(f(i, m - j - 1)) st.insert({i, m - j - 1});
                // n - i - 1, m - j - 1
                if(f(n - i - 1, m - j - 1)) st.insert({n - i - 1, m - j - 1});

            int tot = st.size();
            int cnt = 0;
            for(auto it : st)
            {
                if(a[it.first][it.second] == 1) cnt++;
            }

            if(tot == 1)
            {
                if(a[i][j] == 1) fix1 = true;
                else fix0 = true;
            }
            else if(tot == 2)
            {
                if(cnt == 0 || cnt == 2) continue;
                else
                {
                    one++;
                    ans++;
                    for(auto it : st) a[it.first][it.second] = 1;
                }
            }
            else if(tot == 4)
            {
                if(cnt == 0 || cnt == 4) continue;
                else if(cnt == 1 || cnt == 3)
                {
                    ans++;
                    for(auto it : st) a[it.first][it.second] = 1;
                }
                else
                {
                    two++;
                    ans += 2;
                    for(auto it : st) a[it.first][it.second] = 1;
                }
            }
            }
        }

        int cnt = 0;
        for(int i = 0; i < n; i++)
        {
            for(int j = 0; j < m; j++) if(a[i][j] == 1) cnt++;
        }
        cnt %= 4;

        // cout << fix0 << " " << fix1 << " " << one << endl;

        if(cnt == 0) return ans;
        else if(cnt == 1)
        {
            if(fix1) return ans + 1;
            else if(fix0 && one) return ans + 1;
            else return ans + 3;
        }
        else if(cnt == 2)
        {
            if(one) return ans;
            else return ans + 2;
        }
        else
        {
            if(fix0) return ans + 1;
            else if(fix1 && one) return ans + 1;
            else return ans + 3;
        }
    }
};
