class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int n=grid.size();
        int m=grid[0].size();

        int ans=0;

         for(int i=0;i<n/2;i++){
             for(int j=0;j<m/2;j++){
                 int a=n-i-1;
                 int b=m-j-1;

                 int c=grid[i][j];
                 c+=grid[i][b];
                 c+=grid[a][j];
                 c+=grid[a][b];

                 ans+=min(c,4-c);
             }
         }

        if(m%2 && n%2 && grid[n/2][m/2]){
            ans++;
        }

        int o=0;
        int chg=0;
        if(n%2){
           for(int i=0;i<m/2;i++){
               if(grid[n/2][i]!=grid[n/2][m-i-1]){
                   ans++;
                  chg++;
               }
               else if(grid[n/2][i]==1)
                   o+=2;
           } 
        }

        cout<<ans<<" "<<o<<endl;

         if(m%2){
           for(int i=0;i<n/2;i++){
               if(grid[i][m/2]!=grid[n-i-1][m/2]){
                   ans++;
                  chg++;
               }
               else if(grid[i][m/2]==1)
                   o+=2;
           } 
        }

        if(o%4==0)
            return ans;
        else if(chg>0)
            return ans;
        return ans+2;
    }
};
