class Solution {
    int func(int a,int b,int c,int d){
        int k[2] = {0};
        k[a]++;
        k[b]++;
        k[c]++;
        k[d]++;
        return min(k[0],k[1]);
    }
public:
    int minFlips(vector<vector<int>>& grid) {
        int m = grid.size();
        int n = grid[0].size();
        int cnt = 0;
        int m_mid = m/2;
        int n_mid = n/2;
        for(int i = 0;i<m/2;i++){
            for(int j = 0;j<n/2;j++){
                cnt += func(grid[i][j],grid[m-1-i][j],grid[i][n-1-j],grid[m-1-i][n-1-j]);
            }
        }
        //cout<<cnt<<endl;
        bool row_col = false;
        if(m%2 == 1 && n%2 == 1 && grid[m_mid][n_mid] == 1){
            cnt++;
        }
        int tot = 0;
        if(m%2 == 1){
            for(int j = 0;j<n_mid;j++){
                int a = grid[m_mid][j];
                int b = grid[m_mid][n-1-j];
                if(a == b){
                    if(a == 1)
                        tot += 2;
                }else{
                    cnt += 1;
                    row_col = true;
                }
            }
        }
        if(n%2 == 1){
            for(int i = 0;i<m_mid;i++){
                int a = grid[i][n_mid];
                int b = grid[m-1-i][n_mid];
                if(a == b){
                    if(a == 1){
                        tot += 2;
                    }
                }else{
                    cnt += 1;
                    row_col = true;
                }
            }
        }
        if(tot % 4 != 0){
            if(!row_col){
                cnt += 2;
            }
        }
        return cnt;
    }
};
