class Solution {
public:

bool checkPalindrome(vector<int>&vec){
    int n=vec.size();
    int i=0, j=n-1;

    while(i<=j){
        if(vec[i]!=vec[j])return false;
        i++;
        j--;
    }

    return true;
}

int flips(vector<int>&vec){

    int n=vec.size();
    int i=0, j=n-1, cnt=0;

    while(i<=j){
        if(vec[i]!=vec[j])cnt++;
        i++;
        j--;
    }

    return cnt;

}
    int minFlips(vector<vector<int>>& grid) {
         vector<int> doorii(3, 0);

        int kkk = grid.size();
        int llll = grid[0].size(), ttt = 0;

        if (kkk % 2 == 1 && llll % 2 == 1) {
            if (grid[kkk / 2][llll / 2] == 1) {
                ttt += 1;
            }
        }



        if (kkk % 2 == 1) {
            int p = 0, q = llll - 1;
            while (p < q) {
                int sm = grid[kkk / 2][p] + grid[kkk / 2][q];
                doorii[sm] += 1;
                p += 1;
                q -= 1;
            }
        }

        if (llll % 2 == 1) {
            int p = 0, q = kkk - 1;
            while (p < q) {
                int sm = grid[p][llll / 2] + grid[q][llll / 2];
                doorii[sm] += 1;
                p += 1;
                q -= 1;
            }
        }

        if (doorii[2] % 2 == 1) {
            if (doorii[1] > 0) {
                ttt += doorii[1];
            } else {
                ttt += 2;
            }
        } else {
            ttt += doorii[1];
        }

        for (int jjjj = 0; jjjj < kkk / 2; ++jjjj) {
            for (int iiii = 0; iiii < llll / 2; ++iiii) {
                int p = grid[jjjj][iiii];
                int q = grid[kkk - 1 - jjjj][iiii];
                int r = grid[jjjj][llll - 1 - iiii];
                int s = grid[kkk - 1 - jjjj][llll - 1 - iiii];

                int sm = p + q + r + s;

                if (sm != 0 && sm != 4) {
                    ttt += min(sm, 4 - sm);
                }
            }
        }

        return ttt;


    }
};
