class Solution {
public:
    int minFlips(vector<vector<int>>& arr) {
         int n=arr.size(),ans=0;
        int m=arr[0].size();
        for(int i=0;i<n/2;i++)
        {
            for(int j=0;j<m/2;j++)
            {
                int sum=0,minn=INT_MAX;
                sum+=arr[i][j];
                sum+=arr[n-i-1][j];
                sum+=arr[i][m-j-1];
                sum+=arr[n-i-1][m-j-1];
                minn=min(4-sum,sum);
                ans+=minn;
            }
        }

        int cnt=0,cnt1=0,val=0;
        if(n%2)
        {
            for(int i=0;i<m/2;i++)
            {
                if(arr[n/2][i]!=arr[n/2][m-i-1])
                {
                    cnt++;
                }
                else if(arr[n/2][i]==1)
                {
                    val++;
                }
            }
        }
          if(m%2)
        {
            for(int i=0;i<n/2;i++)
            {
                if(arr[i][m/2]!=arr[n-i-1][m/2])
                {
                    cnt1++;
                }
                else if(arr[i][m/2]==1)
                    val++;
            }
        }
        if(n%2 and m%2 and arr[n/2][m/2])
        {
            ans++;
        }
        if(val%2 and cnt1+cnt==0)
            ans+=2;


        return ans+cnt+cnt1;
    }
};
