class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int ans=0,m=grid.size(),n=grid[0].size(),o=0;
        for(int i=0;i<m/2;i++){
            for(int j=0;j<n/2;j++){
                int k=grid[i][j]+grid[m-1-i][j]+grid[i][n-1-j]+grid[m-1-i][n-1-j];
                if(k>4-k)o+=4;
                ans+=min(k,4-k);
            }
        }
        int a=0,b=0,o1=0,o2=0;
        if(m&1){
            for(int i=0;i<n/2;i++)if(grid[m/2][i]!=grid[m/2][n-1-i])a++;else if(grid[m/2][i]==1)o1+=2;
        }
        if(n&1){
            for(int i=0;i<m/2;i++)if(grid[i][n/2]!=grid[m-1-i][n/2])b++;else if(grid[i][n/2]==1)o2+=2;
        }
        // bool t=0;
        // if(o1%4==2&&!a&&!ans)a=2,t=1;
        // if(o2%4==2&&!b&&(!t||(t&&ans>1)))b=2;
        o+=o1+o2;
        if(o%4==2&&!a&&!b)ans+=2;
        if(m%2==1&&n%2==1&&grid[m/2][n/2])ans++;
        return ans+a+b;
    }
};
