class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int m = grid.size(), n = grid[0].size();
        int st = 0, ed = m - 1;
        int ans = 0;
        while(st < ed) {
            int ist = 0, ied = n - 1;
            while(ist < ied) {
                int oneCnt = 0, zeroCnt = 0;
                grid[st][ist] == 0 ? zeroCnt++ : oneCnt++;
                grid[st][ied] == 0 ? zeroCnt++ : oneCnt++;
                grid[ed][ist] == 0 ? zeroCnt++ : oneCnt++;
                grid[ed][ied] == 0 ? zeroCnt++ : oneCnt++;
                ans += min(oneCnt, zeroCnt);
                ist++; ied--;
            }
            st++; ed--;
        }

        int oneCnt = 0;
        int misMatch = 0;
        if(m % 2 != 0) {
            int ist = 0, ied = n - 1;
            while(ist < ied) {
                if(grid[m/2][ist] != grid[m/2][ied]) {
                    misMatch++;
                } else {
                    if(grid[m/2][ist] == 1) {
                        oneCnt += 2;
                    }
                }
                ist++; ied--;
            }
        }
        if(n % 2 != 0) {
            int ist = 0, ied = m - 1;
            while(ist < ied) {
                if(grid[ist][n/2] != grid[ied][n/2]) {
                    misMatch++;
                } else {
                    if(grid[ist][n/2] == 1) {
                        oneCnt += 2;
                    }
                }
                ist++; ied--;
            }
        }
        if(m % 2 != 0 && n % 2 != 0) {
            if(grid[m/2][n/2]) {
                ans++;
            }
        }
        if(oneCnt % 4 != 0) {
            if(misMatch > 0) 
            {
                return ans + misMatch;
            } else {
                return ans + 2;
            }
        }
        return ans + misMatch;
    }
};
