class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int ans = 0;
        int m = grid.size(), n = grid[0].size();
        for(int i = 0; i < m/2; i++){
            for(int j = 0; j < n/2; j++){
                vector<int> freq(2, 0);
                freq[grid[i][j]]++;
                freq[grid[m-1-i][j]]++;
                freq[grid[i][n-1-j]]++;
                freq[grid[m-1-i][n-1-j]]++;
                if(freq[0] > freq[1]){
                    ans+=freq[1];
                }
                else if(freq[0] == freq[1]){
                    ans+=freq[0];
                }
                else{
                    ans+=freq[0];
                }
            }
        }

        bool odd1pairs = false;
        if(m&1){
            for(int j = 0; j < n/2; j++){
                if(grid[m/2][j] == 1 && grid[m/2][n-1-j] == 1)
                    odd1pairs = !odd1pairs;
            }
        }
        if(n&1){
            for(int i = 0; i < m/2; i++){
                if(grid[i][n/2] == 1 && grid[m-1-i][n/2] == 1)
                    odd1pairs = !odd1pairs;
            }
        }

        if(m&1){
            for(int j = 0; j < n/2; j++){
                ans+=(grid[m/2][j]!=grid[m/2][n-1-j]);
                if(grid[m/2][j]!=grid[m/2][n-1-j] && odd1pairs)
                    odd1pairs = false;
            }
        }
        if(n&1){
            for(int i = 0; i < m/2; i++){
                ans+=(grid[i][n/2]!=grid[m-1-i][n/2]);
                if(grid[i][n/2]!=grid[m-1-i][n/2] && odd1pairs)
                    odd1pairs = false;
            }
        }
        if((n&1)&&(m&1))
            ans+=grid[m/2][n/2];
        if(odd1pairs)
            ans+=2;
        return ans;
    }
};
