class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int rows=grid.size();
        int cols=grid[0].size();

        int count1=0;
        for(int i=0;i<rows/2;i++)
        {
            for(int j=0;j<cols/2;j++)
            {
                int x=grid[i][j];
                int count=1;
                if(grid[rows-1-i][j] == x)
                count++;

                if(grid[i][cols-1-j]==x)
                count++;

                if(grid[rows-1-i][cols-1-j]==x)
                count++;

                count1+=min(count,4-count);
            }
        }
        int c1=0;
        int cnt2=0;
        if(rows%2==1)
        for(int j=0;j<cols/2;j++)
        {
            if(grid[rows/2][j]!=grid[rows/2][cols-1-j])
            {
                cnt2++;
            }
            else
            {
                if(grid[rows/2][j]==1)
                c1+=2;
            }
        }

            count1+=cnt2;




        int c2=0;
        int cnt3=0;
        if(cols%2==1)
        for(int i=0;i<rows/2;i++)
        {
            if(grid[i][cols/2]!=grid[rows-1-i][cols/2])
            {
                cnt3++;
            }
            else
            {
                if(grid[i][cols/2]==1)
                c2+=2;
            }
        }


            count1+=cnt3;




        if((c1+c2)%4!=0 && (cnt2+cnt3)<1)
        {
            count1+=2;
        }
        if(rows%2==1 && cols%2==1)
        {
            if(grid[rows/2][cols/2]==1)
            count1+=1;
        }
        return count1;
    }
};
