function minFlips(grid: number[][]): number {
    let numRws = grid.length;
    let numCls = grid[0].length;

    let minFlipsCount = 0;

    for (let row = 0; row < Math.floor(numRws / 2); row++) {
        for (let col = 0; col < Math.floor(numCls / 2); col++) {
            let elements = [
                grid[row][col],
                grid[numRws - row - 1][col],
                grid[row][numCls - col - 1],
                grid[numRws - row - 1][numCls - col - 1]
            ];

            let onesCount = elements.reduce((sum, val) => sum + val, 0);

            minFlipsCount += Math.min(onesCount, 4 - onesCount);
        }
    }

    let possibleFlips = 0;
    let oddCount = 0;

    if (numRws % 2 !== 0) {
        for (let col = 0; col < Math.floor(numCls / 2); col++) {
            if (grid[Math.floor(numRws / 2)][col] !== grid[Math.floor(numRws / 2)][numCls - col - 1]) {
                minFlipsCount += 1;
                possibleFlips += 1;
            } else {
                if (grid[Math.floor(numRws / 2)][col] === 1) {
                    oddCount += 1;
                }
            }
        }
    }


    if (numCls % 2 !== 0) {
        for (let row = 0; row < Math.floor(numRws / 2); row++) {
            if (grid[row][Math.floor(numCls / 2)] !== grid[numRws - row - 1][Math.floor(numCls / 2)]) {
                minFlipsCount += 1;
                possibleFlips += 1;
            } else {
                if (grid[row][Math.floor(numCls / 2)] === 1) {
                    oddCount += 1;
                }
            }
        }
    }
    if (numRws % 2 !== 0 && numCls % 2 !== 0 && grid[Math.floor(numRws / 2)][Math.floor(numCls / 2)] === 1) {
        minFlipsCount += 1;
    }

    if (possibleFlips === 0 && oddCount % 2 !== 0) {
        minFlipsCount += 2;
    }

    return minFlipsCount;
}
