class Solution {
private:
    void trans(int ans[], vector<pair<int,int> > ops) {
        int cur[4];
        for(int i = 0; i < 4; i++) {
            cur[i] = ans[i];
            ans[i] = 1000000000;
        }

        for(int i = 0; i < 4; i++) {
            for(auto op : ops) {
                auto &t = ans[(i + op.first) % 4];
                t = min(t, cur[i] + op.second);
            }
        }
    }
public:
    int minFlips(vector<vector<int>>& grid) {
        int n = grid.size();
        int m = grid[0].size();
        int ans[4] = {0, 1000000000, 1000000000, 1000000000};

        for(int i = 0; i <= n - 1 - i; i++) {
            for(int j = 0; j <= m - 1 - j; j++) {
                vector<pair<int,int> > ops;
                int cnt[2] = {0}, tot = 0;

                int vi[] = {i, n - 1 - i};
                int vj[] = {j, m - 1 - j};

                for(int ti = 0; ti < (vi[0] == vi[1] ? 1 : 2); ti++) {
                    for(int tj = 0; tj < (vj[0] == vj[1] ? 1 : 2); tj++) {
                        cnt[grid[vi[ti]][vj[tj]]]++;
                        tot++;
                    }
                }

                for(int target = 0; target < 2; target++) {
                    ops.push_back(make_pair(target * tot, cnt[target ^ 1]));
                }

                trans(ans, ops);
            }
        }
        return ans[0];
    }
};
