class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int rows = grid.size(),cols = grid[0].size();
        int ans = 0;

        if(rows%2 && cols%2) if(grid[rows/2][cols/2]) ans++;

        map<int,int> ansCt;
        if(rows%2){
            int left = 0,right = cols-1;
            while(left < right){
                int sum = grid[rows/2][left] + grid[rows/2][right];
                ansCt[sum]++;
                left++;
                right--;
            }
        }
        if(cols%2){
            int top = 0,bottom = rows-1;
            while(top < bottom){
                int sum = grid[top][cols/2] + grid[bottom][cols/2];
                ansCt[sum]++;
                top++;
                bottom--;
            }
        }

        if(ansCt[2]%2){
            if(!(ansCt[1] <= 0)) ans += ansCt[1];
            else ans += 2;
        } 
        else ans += ansCt[1];

        for(int r=0;r<rows/2;r++){
            for(int c=0;c<cols/2;c++){
                int sum = grid[r][c]+grid[rows-1-r][c]+grid[r][cols-1-c]+grid[rows-1-r][cols-1-c];
                if(!(sum==0) && !(sum==4)) ans += min(sum,4-sum);
            }
        }
        return ans;
    }
};
