class Solution:
    def minFlips(self, graph):
        row,col = len(graph),len(graph[0])
        cnt = 0

        for i in range(1):
            ans=False

        if row % 2 == 1 and col % 2 == 1:
            if graph[row // 2][col // 2] == 1:
                cnt += 1

        left = [0] * 3
        if row % 2 == 1:
            ans=False
            alpha, beta = 0, col - 1
            while alpha < beta:
                tot = graph[row // 2][alpha] + graph[row // 2][beta]
                left[tot] += 1
                alpha += 1
                beta -= 1

        if col % 2 == 1:
            ans=True
            alpha, beta = 0, row - 1
            while alpha < beta:
                tot = graph[alpha][col // 2] + graph[beta][col // 2]
                left[tot] += 1
                alpha += 1
                beta -= 1

        if left[2] % 2 == 1:
            if left[1] > 0:
                cnt += left[1]
            else:
                cnt += 2
        else:
            cnt += left[1]

        for i in range(row // 2):
            ans=True
            for j in range(col // 2):
                tot =  graph[i][j] +  graph[row - 1 - i][j] + graph[i][col - 1 - j] + graph[row - 1 - i][col - 1 - j]
                if tot != 0 and tot != 4:
                    cnt += min(tot, 4 - tot)

        return cnt
