class Solution {
    public int minFlips(int[][] grid) {
        int m = grid.length;
        int n = grid[0].length;

        int res = 0;
        for (int i = 0, j = m - 1; i < j; i++, j--) {
            for (int u = 0, v = n - 1; u < v; u++, v--) {
                int x = grid[i][u] + grid[j][u] + grid[i][v] + grid[j][v];
                if (x == 0 || x == 4) {
                    res += 0;
                } else if (x == 1 || x == 3) {
                    res += 1;
                } else {
                    res += 2;
                }
            }
        }

        int cnt = 0, dif = 0;
        if (m % 2 == 1) {
            int t = m / 2;
            for (int u = 0, v = n - 1; u < v; u++, v--) {
                int x = grid[t][u] + grid[t][v];
                if (x == 2) {
                    cnt += 2;
                } else if (x == 1) {
                    dif += 1;
                }
            }
        }
        if (n % 2 == 1) {
            int t = n / 2;
            for (int u = 0, v = m - 1; u < v; u++, v--) {
                int x = grid[u][t] + grid[v][t];
                if (x == 2) {
                    cnt += 2;
                } else if (x == 1) {
                    dif += 1;
                }
            }
        }
        if (dif == 0 && cnt % 4 == 2) {
            res += 2;
        }
        res += dif;

        if (m % 2 == 1 && n % 2 == 1 && grid[m / 2][n / 2] == 1) {
            res += 1;
        }
        return res;
    }
}
