class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {

        int out = 0;

        int row = grid.size();
        int col = grid[0].size();

        if(row == col && row == 1) return grid[0][0];

        // int cnt1 = 0;

        // for(int i = 0; i < row; i++){
        //     for(int j = 0; j < col; j++){
        //         if (grid[i][j] == 1) cnt1++;
        //     }
        // }

        for(int i = 0; i < row/2; i++){
            for(int j = 0; j<col/2; j++){
                map<int, int> m;
                m[grid[i][j]]++;
                m[grid[row - 1 - i][j]]++;
                m[grid[i][col - 1 - j]]++;
                m[grid[row - 1 - i][col - 1 - j]]++;
                if(m[0] < m[1]){
                    // cnt1 += m[0];
                    out += m[0];
                }
                else{
                    // cnt1 -= m[1];
                    out += m[1];
                }
            }
        }

        int flipcnt = 0;
        int cnt1 = 0;

        // cout << out << " " << row << " " << col  << endl;

        if (row % 2){
            for(int i = 0; i < col/2; i++){
                // cout << i << " " << grid[(row/2) + 1][i] << " " << grid[(row/2) + 1][col-i-1] << endl;
                if (grid[row/2][i] != grid[row/2][col - 1- i]){
                    flipcnt++;
                    cnt1++;
                    out++;
                }
                else if(grid[row/2][i] == 1) cnt1 += 2;
            }
        }
        // cout << out << " " << row << " " << col  << endl;
        // cout << out << " " << cnt1 << " " << flipcnt << endl;

        if (col % 2){
            for(int i = 0; i < row/2; i++){
                // cout << i << " " << grid[i][col/2 + 1] << " " <<  grid[row - 1- i][col/2 + 1] << endl;
                if (grid[i][col/2] != grid[row - 1- i][col/2]){
                    flipcnt++;
                    cnt1++;
                    out++;
                }
                else if(grid[i][col/2] == 1) cnt1 += 2;
            }
        }

        // cout << out << " " << cnt1 << " " << flipcnt << endl;

        if((row %2) & (col % 2)){
            if (grid[row/2][col/2] == 1){
                out++;
            }
        }

        int mod = cnt1 % 4;

        if(mod == 0){}
        else if(mod == 1 && flipcnt >= 1){}
        else if(mod == 2 && flipcnt >= 2){}
        else if(mod == 3 && flipcnt >= 1){}
        else if(mod == 2){ out += 2;}




        return out;
    }
};
