public class Solution {

    public int minFlips(int[][] grid) {
        int r = grid.length;
        int c = grid[0].length;
        int ans = 0;
        int[] sh = new int[3];

        if (r % 2 == 1 && c % 2 == 1 && grid[r / 2][c / 2] == 1) {
            ans++;
        }

        if (r % 2 == 1) {
            int l = 0, rIdx = c - 1;
            while (l < rIdx) {
                int s = grid[r / 2][l] + grid[r / 2][rIdx];
                sh[s]++;
                l++;
                rIdx--;
            }
        }

        if (c % 2 == 1) {
            int t = 0, bIdx = r - 1;
            while (t < bIdx) {
                int s = grid[t][c / 2] + grid[bIdx][c / 2];
                sh[s]++;
                t++;
                bIdx--;
            }
        }

        if (sh[2] % 2 == 1) {
            ans += sh[1] > 0 ? sh[1] : 2;
        } else {
            ans += sh[1];
        }

        int i = 0;
        while (i < r / 2) {
            int j = 0;
            while (j < c / 2) {
                int a = grid[i][j];
                int b = grid[r - 1 - i][j];
                int x = grid[i][c - 1 - j];
                int y = grid[r - 1 - i][c - 1 - j];
                int sm = a + b + x + y;
                if (sm != 0 && sm != 4) {
                    ans += Math.min(sm, 4 - sm);
                }
                j++;
            }
            i++;
        }

        return ans;
    }
}
