class Solution {
    // 0 1 2 3 : 4
public:
    int minFlips(vector<vector<int>>& grid) {
        int n = grid.size(), m = grid[0].size();
        int ans = 0;

        auto count = [&](vector<int> cnt) {
            int c0 = 0, c1 = 0;
            for (auto x : cnt) {
                if (x == 0) {
                    c0++;
                } else {
                    c1++;
                }
            }
            return min(c0, c1);
        };

        for (int i = 0; i < n / 2; i++) {
            for (int j = 0; j < m / 2; j++) {
                int a = grid[i][j], b = grid[n - 1 - i][j], c = grid[i][m - 1 - j], d = grid[n - 1 - i][m - 1 - j];
                ans += count(vector<int>{a, b, c, d});
            }
        }

        int cnt = 0, res = 0;
        if (n % 2 == 1) {
            int i = n / 2;
            int j = 0, k = m - 1;
            while (j < k) {
                if (grid[i][j] != grid[i][k]) {
                    res++;
                } else if (grid[i][j] == 1) {
                    cnt += 2;
                }
                j++, k--;
            }
        }
        if (m % 2 == 1) {
            int i = m / 2;
            int j = 0, k = n - 1;
            while (j < k) {
                if (grid[j][i] != grid[k][i]) {
                    res++;
                } else if (grid[j][i] == 1) {
                    cnt += 2;
                }
                j++, k--;
            }
        }
        if (m % 2 == 1 and n % 2 == 1 and grid[n / 2][m / 2] == 1) {
            cnt++;
        }

        // cout << ans << " " << cnt << " " << res << "\n";

        if (cnt % 4 == 1) {
            ans += res + 1;
        } else if (cnt % 4 == 2) {
            if (res >= 1) {
                ans += res;
            } else {
                ans += res + 2;
            }
        } else if (cnt % 4 == 3) {
            ans++;
            if (res >= 1) {
                ans += res;
            } else {
                ans += res + 2;
            }
        } else {
            ans += res;
        }
        return ans;
    }
};
