class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int ans=0,n=grid.size(),m=grid[0].size();
        if(n==1){
            int oc=0,occ=0;
            for(int i=0;i<m/2;i++){
                if(grid[0][i]==1 && grid[0][m-i-1]==1){
                    oc++;
                }
                else{
                    if(grid[0][i]==1 || grid[0][m-i-1]==1){
                        occ++;
                    }
                    ans+=grid[0][i]+grid[0][m-i-1];
                }
            }
            if(m%2!=0){
                ans+=grid[0][m/2];
            }
            if(oc%2!=0){
                if(oc%2==1 && occ>=1){
                    // ans++;
                }
                else
                ans+=2;
            }
            return ans;
        }
        if(m==1){
            int oc=0,occ=0;
            for(int i=0;i<n/2;i++){
                if(grid[i][0]==1 && grid[n-i-1][0]==1){
                    oc++;
                }
                else{
                    if(grid[i][0]==1 || grid[n-i-1][0]==1){
                        occ++;
                    }
                    ans+=grid[i][0]+grid[n-i-1][0];
                }
            }
            if(n%2!=0){
                ans+=grid[n/2][0];
            }

            if(oc%2!=0){
                // cout<<oc<<" "<<occ<<endl;
                if(oc%2==1 && occ>=1){
                    // ans++;
                }
                else
                ans+=2;
            }
            return ans;
        }

        //4 n/2 = 2  0,1,2,3
        //5 n/2 = 2  0,1,2,3,4
        for(int i=0;i<(n/2);i++){
            int l=0,r=m-1;
            int cnt=0;
            while(l<r){
                int oc=0;
                oc+=grid[i][l]+grid[i][r]+grid[n-i-1][l]+grid[n-i-1][r];
                cnt+=min(oc,4-oc);
                l++;
                r--;
            }
            ans+=cnt;
        }
        int occc=0,occcc=0;
        if(n%2!=0){
            for(int i=0;i<m/2;i++){
                if(grid[n/2][i]==1 && grid[n/2][m-i-1]==1){
                    occc++;
                }
                else{
                    if(grid[n/2][i]==1 || grid[n/2][m-i-1]==1)
                    {
                        occcc++;
                    }
                    ans+=grid[n/2][i]+grid[n/2][m-i-1];
                }
            }
        }
        if(m%2!=0 && n%2!=0){
            if(grid[n/2][m/2]==1)ans++;
        }
        if(m%2!=0){
            for(int i=0;i<n/2;i++){
                if(grid[i][m/2]==1 && grid[n-i-1][m/2]==1){
                    occc++;
                }
                else{
                    if(grid[i][m/2]==1 || grid[n-i-1][m/2]==1)
                    {
                        occcc++;
                    }
                    ans+=grid[i][m/2]+grid[n-i-1][m/2];
                }
            }
        }
        if(occc%2!=0){
            if(occc%2==1 && occcc>=1){
                // ans++;
            }
            else
            ans+=2;
        }
        return ans;
    }
};

