class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int n = grid.size();
        int m = grid[0].size();

        int cnt=0;

    for(int j=0;j<m/2;j++){
        int l=j,r=m-1-j;

        for(int i=0;i<n/2;i++){
            int c=0;
            if(grid[i][l]==1) c++;
            if(grid[i][r]==1) c++;
            if(grid[n-i-1][l]==1) c++;
            if(grid[n-i-1][r]==1) c++;

            if(c==2) cnt+=2;
            else cnt+=(c%2);
        }
    }

        if(n%2==0 && m%2==0);
        else if(n%2!=0 && m%2==0){
            int c=0,rc=0;
            int l=0,r=m-1;
            while(l<r){
                if(grid[n/2][l]!=grid[n/2][r]) c++;
                if(grid[n/2][l]==1 && grid[n/2][r]==1) rc+=2;
                l++;r--;
            }
            cnt+=c;
            if(rc%4==2 && c==0) cnt+=2;
        }
        else if(m%2!=0 && n%2==0){
            int c=0,rc=0;
            int l=0,r=n-1;
            while(l<r){
                if(grid[l][m/2]!=grid[r][m/2]) c++;
                if(grid[l][m/2]==1 && grid[r][m/2]==1) rc+=2;
                l++;r--;
            }
            cnt+=c;   
            if(rc%4==2 && c==0) cnt+=2;    
        }
        else{
            int c=0,rc=0;
            int l=0,r=m-1;
            while(l<r){
                if(grid[n/2][l]!=grid[n/2][r]) c++;
                if(grid[n/2][l]==1 && grid[n/2][r]==1) rc+=2;
                l++;r--;
            }

            l=0,r=n-1;
            while(l<r){
                if(grid[l][m/2]!=grid[r][m/2]) c++;
                if(grid[l][m/2]==1 && grid[r][m/2]==1) rc+=2;
                l++;r--;
            }
            cnt+=c;    
            if(rc%4==2 && c==0) cnt+=2;

            if(grid[n/2][m/2]==1) cnt++;          

        }

        return cnt;     

    }
};
