class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int ans = 0;
        int m = grid.size();
        int n = grid[0].size();

        // 4的倍数位置
        for (int i = 0; i < m / 2; i++) {
            for (int j = 0; j < n / 2; j++) {
                int num[2] = {0};
                num[grid[i][j]]++;
                num[grid[i][n - 1 - j]]++;
                num[grid[m - 1 - i][j]]++;
                num[grid[m - 1 - i][n - 1 - j]]++;

                if (num[0] == 1 || num[0] == 3) {
                    ans += 1;
                } else if (num[0] == 2) {
                    ans += 2;
                }
            }
        }

        //cout<<ans<<endl;

        // 中心点
        if (m % 2 != 0 && n % 2 != 0 && grid[m / 2][n / 2] == 1)
            ans++;

        // 十字线
        int num=0;
        int num1=0;
        if (n % 2 != 0) {
            for (int i = 0; i < m / 2; i++) {
                if (grid[i][n / 2] != grid[m - 1 - i][n / 2]){
                    num++;
                    num1++;
                }
                else if(grid[i][n / 2]==1)
                {
                    num1+=2;
                }
            }
        }

        //cout<<ans<<endl;

        if (m % 2 != 0) {
            for (int i = 0; i < n / 2; i++) {
                if (grid[m / 2][i] != grid[m / 2][n - 1 - i]){
                    num++;
                    num1++;
                }
                else if(grid[m / 2][i]==1)
                {
                    num1+=2;
                }
            }
        }

        if(num>0) ans+=num;
        else if(num1%4>0) ans+=2;

        return ans;
    }
};
