class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int ans=0;
        int cnt=0,n=grid.size(),m=grid[0].size(),x=0;
        for(auto i:grid)
            for(auto j:i) if(j) ++cnt;
        for(int i=0;i<n/2;i++) 
        {
            for(int j=0;j<m/2;j++) 
            {
                int c=0;
                if(grid[i][j]) ++c;
                if(grid[n-i-1][j]) ++c;
                if(grid[i][m-j-1]) ++c;
                if(grid[n-i-1][m-j-1]) ++c;
                ans+=min(c,4-c);
            }
        }
        int c=0;
        if(m%2) 
        {
            for (int i=0;i<n/2;i++) 
            {
                if(grid[i][m/2]!=grid[n-i-1][m/2]) {++ans; ++x;} 
                else if(grid[i][m/2]) ++c;
            }
        }
        if(n%2) 
        {
            for (int i=0;i<m/2;i++) 
            {
                if (grid[n/2][i]!=grid[n/2][m-i-1]) {++ans;++x;}
                else if(grid[n/2][i]) ++c;
            }
        }
        if (n%2 && m%2 && grid[n/2][m/2]) ++ans;
        if (!x && c%2) ans+=2;
        return ans;
    }
};
