class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int n=grid.size();
        int m=grid[0].size();
        vector<vector<int>>vec=grid;
        int ans=0;
        int zero_to_one=0;
        int one_to_zero=0;
        int z=0;
        for(int i=0;i<n/2;i++){
            for(int j=0;j<m/2;j++){
                int one=grid[i][j]+grid[i][m-j-1]+grid[n-i-1][j]+grid[n-i-1][m-j-1];
                int zero=4-one;
                int l=min(one,zero);
                if(zero==1){
                    zero_to_one+=zero;
                   vec[i][j]=1;
                    vec[i][m-j-1]=1;
                    vec[n-i-1][j]=1;
                    vec[n-i-1][m-j-1]=1;

                }
                if(one==1){one_to_zero+=one;
                 vec[i][j]=0;
                    vec[i][m-j-1]=0;
                    vec[n-i-1][j]=0;
                    vec[n-i-1][m-j-1]=0;
                }
                if(one==2){z+=2;
                    vec[i][j]=1;
                    vec[i][m-j-1]=1;
                    vec[n-i-1][j]=1;
                    vec[n-i-1][m-j-1]=1;
                 }
             ans+=l;   
            }
        }
        int totfour=0;
        for(int i=0;i<n;i++){
            for(int j=0;j<m;j++){
            if(vec[i][j]==1)totfour++;
            }
        }int yo=0;
        if(n%2==1){
          for(int j=0;j<m/2;j++){
            if(grid[n/2][j]!=grid[n/2][m-j-1])yo++;
          }
        }
        int uo=0;
        if(m%2==1){
            for(int i=0;i<n/2;i++){
            if(grid[i][m/2]!=grid[n-i-1][m/2])yo++;
          }
        }
        if(n%2==1 && m%2==1){
            if(grid[n/2][m/2]==1){ans++;
            totfour--;
            }
        }
        if(yo)return ans+yo;
        else{
            if(totfour%4==2)return ans+2;
            else return ans;
        }

    }
};
