class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int n=grid.size(),m=grid[0].size(),answer=0,tmp=0,a=0;
        double n2=n/2.0,m2=m/2.0;

        if((n&1)&&(m&1)&&grid[n/2][m/2]==1){
            answer++;
            grid[n/2][m/2]=0;
        }
        for(int i=0;i<n2;i++){
            for(int j=0;j<m2;j++){
                if(i==n-1-i&&j==m-1-j)break;
                if(i==n-1-i){
                    int k=0;
                    if(grid[i][j]==1)k++;
                    if(grid[i][m-1-j]==1)k++;
                    if(k==1)answer+=1,a++;
                    else tmp+=k;
                }
                else if(j==m-1-j){
                    int k=0;
                    if(grid[i][j]==1)k++;
                    if(grid[n-1-i][j]==1)k++;
                    if(k==1)answer+=1,a++;
                    else tmp+=k;
                }
                else{
                    int k=0;
                    if(grid[i][j]==1)k++;
                    if(grid[n-1-i][j]==1)k++;
                    if(grid[i][m-1-j]==1)k++;
                    if(grid[n-1-i][m-1-j]==1)k++;

                    if(k==1||k==3)answer+=1;
                    else if(k==2)answer+=2;
                }
            }
        }
        if(tmp%4!=0&&a==0)answer+=2;
        return answer;
    }
};
