class Solution {
    public int minFlips(int[][] grid) {
        int n = grid.length;
        int m = grid[0].length;

        if(n == 1 && m == 1) return grid[0][0];

        int ans = 0;
        int ones = 0;
        int midCount = 0;
        int oddCnt = 0;
        for(int i=0;i<=(n-1)/2;i++){
            for(int j=0;j<=(m-1)/2;j++){
                // System.out.println(i + " " + j);
                int cnt = 0; 
                int i1 = i;
                int j1 = j;
                cnt += grid[i1][j1];

                int i2 = i;
                int j2 = m-j-1;
                cnt += grid[i2][j2];

                int i3 = n-i-1;
                int j3 = j;
                cnt += grid[i3][j3];

                int i4 = n-i-1;
                int j4 = m-j-1;
                cnt += grid[i4][j4];

                // System.out.println(i1 + " " + i3 + " " + j1 + " " + j2 + " " + m);
                if(i1 != i3 && j1 != j2) {
                    System.out.println(cnt);
                    ans += Math.min(cnt, 4 - cnt);
                }
                else if(i1 == i3 && j1 != j2) {
                    int val1 = grid[i1][j1];
                    int val2 = grid[i2][j2];
                    if(val1 != val2) {
                        oddCnt += 1;
                    }
                    else {
                        ones += val1;
                        ones += val2;
                    }
                } else if(j1 == j4 && i1 != i4) { 
                    int val1 = grid[i1][j1];
                    int val2 = grid[i4][j4];
                    if(val1 != val2) {
                        oddCnt += 1;
                    }
                    else {
                        ones += val1;
                        ones += val2;
                    }
                }
                else {
                    midCount += grid[i1][j1];
                }
            }
        }

        int divFour = ones + midCount;
        int mod = divFour % 4;

        System.out.println(ans + " " + oddCnt + " " + midCount + " " + ones);

        if(mod == 0) {
            return ans + oddCnt;
        } else if(mod == 1) {
            return ans + oddCnt + midCount;
        } else if(mod == 2) {
            if(oddCnt == 0) return ans + 2;
            else return ans + oddCnt;
        } else {
            if(oddCnt == 0) return ans + midCount + 2;
            else return ans + oddCnt + midCount;
        }
    }
}
