class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int n=grid.size();
        int m=grid[0].size();
        int l=n/2,k=m/2;
        int ans=0; 
        if (n&1 && m&1) { 
            if (grid[l][k]) { 
                ans++; 
            } 
        } 
        int x=4;
        vector<int>v(x-1, 0); 
        if (n&1) { 
            int a = 0, b = m;
            b--; 
            while (a < b) { 
                int sm = grid[n / 2][a] + grid[n / 2][b]; 
                v[sm]++; 
                a ++; 
                b--; 
            } 
        } 
        if (m&1) { 
            int a = 0, b = n;
            b--; 
            while (a < b) { 
                int sm = grid[a][m / 2] + grid[b][m / 2]; 
                v[sm] += 1; 
                a += 1; 
                b -= 1; 
            } 
        } 

        if (v[2] &1) { 
            if (v[1] > 0) { 
                ans += v[1]; 
            } else { 
                ans += 2; 
            } 
        } else { 
            ans += v[1]; 
        } 

        for (int i = 0; i < n / 2; i++) { 
            for (int j = 0; j < m / 2; j++) { 
                int a = grid[i][j]; 
                int b = grid[n -i-1][j]; 
                int c = grid[i][m -j-1]; 
                int d = grid[n - 1 - i][m - 1 - j]; 

                int val = a + b;
                val+=(c+d); 

                if (val != 0 && val != 4) { 
                    ans += min(val, 4 - val); 
                } 
            } 
        } 

        return ans; 
    }
};
