class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int n=grid.size(),m=grid[0].size();
        int res=0,cnt=0;
        for(int i=0;i<n/2;++i){
            for(int j=0;j<m/2;++j){
                int a=n-i-1,b=m-j-1;
                int x=0;
                if(grid[i][j]==1)x++;
                if(grid[i][b]==1)x++;
                if(grid[a][j]==1)x++;
                if(grid[a][b]==1)x++;
                if(x<2)res+=x;
                else res+=4-x;
            }
        }
        cout<<res<<'\n';
        int sum=0;
        if(n&1){
            int i=n/2;
            for(int j=0;j<m/2;++j){
                if(grid[i][j]!=grid[i][m-j-1]){
                    cnt++;
                }else if(grid[i][j]==1){
                    sum+=2;
                }
            }
        }
        //cout<<res<<'\n';
        if(m&1){
            int j=m/2;
            for(int i=0;i<n/2;++i){
                if(grid[i][j]!=grid[n-i-1][j]){
                    cnt++;
                }else if(grid[i][j]==1){
                    sum+=2;
                }
            }
        }
        res+=cnt;
        //cout<<res<<'\n';
        if(sum%4!=0){
            if(cnt==0)res+=2;
        }
        if((n&1)&&(m&1))if(grid[n/2][m/2]==1)res++;
        return res;
    }
};
