class Solution {
    public int minFlips(int[][] grid) {
        int m=grid.length,n=grid[0].length,res=0;
        if((m&1)==1 && (n&1)==1){
            if(grid[m/2][n/2]==1)
                res++;
        }
        int[]temp=new int[3];
        int a=0,b=0,c=0;
        if((m&1)==1){
            int x=0,y=n-1;
            while(y>x){
                int sum=grid[m/2][x]+grid[m/2][y];
                temp[sum]++;
                y--;
                x++;
            }
        }
        if((n&1)==1){
            int x=0,y=m-1;
            while(y>x){
                int sum=grid[x][n/2]+grid[y][n/2];
                temp[sum]++;
                y--;
                x++;
            }
        }
        if((temp[2]&1)!=1){
            res+=temp[1];
        } 
        else
            if(temp[1]==0)
                res+=2;                
            else
                res+=temp[1];
        for(int i=0;i<m/2;i++){
            for(int j=0;j<n/2;j++){
                int sum=grid[i][j]+grid[m-1-i][j]+grid[i][n-1-j]+grid[m-1-i][n-1-j];
                if(sum!=0 && sum!=4){
                    res+=Math.min(4-sum,sum);
                }
            }
        }
        return res;
    }
}
