class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int m = grid.size();
        int n = grid[0].size();
        // int cnt1=0;
        // for(int i=0;i<m;i++){
        //     for(int j=0;j<n;j++){
        //         if(grid[i][j] == 1) cnt1++;
        //     }
        // }
        // int rem = cnt1%4;
        int flip = 0;
        int i=0, j=m-1;
        while(i<j){
            int k=0, l=n-1;
            while(k<l){
                int cnt0=0;
                int cnt1=0;
                if(grid[i][k]==0){
                    cnt0++;
                }else{
                    cnt1++;
                }
                if(grid[i][l]==0){
                    cnt0++;
                }else{
                    cnt1++;
                }
                if(grid[j][k]==0){
                    cnt0++;
                }else{
                    cnt1++;
                }
                if(grid[j][l]==0){
                    cnt0++;
                }else{
                    cnt1++;
                }
                // cout << cnt0 << " " << cnt1 << endl;
                flip += min(cnt0, cnt1);
                k++;
                l--;
            }
            i++;
            j--;
        }
        if(m%2==0 && n%2==1){
            int c = n/2;
            int i=0, j=m-1;
            int cnt=0;
            int flips = 0;
            while(i<j){
                if(grid[i][c] == grid[j][c]){
                    if(grid[i][c] == 1) cnt+=2;
                }else{
                    flips++;
                }
                i++;
                j--;
            }
            if(cnt%4 == 0){
                flip += flips;
            }else{
                if(flips >= 1){
                    flip += flips;
                }else{
                    flip += 2;
                }
            }
        }else if(n%2==0 && m%2==1){
            int r = m/2;
            int i=0, j=n-1;
            int cnt=0;
            int flips = 0;
            while(i<j){
                if(grid[r][i] == grid[r][j]){
                    if(grid[r][i] == 1) cnt+=2;
                }else{
                    flips++;
                }
                i++;
                j--;
            }
            if(cnt%4 == 0){
                flip += flips;
            }else{
                if(flips >= 1){
                    flip += flips;
                }else{
                    flip += 2;
                }
            }
        }else if(m%2 == 1 && n%2 == 1){
            int c = n/2;
            int i=0, j=m-1;
            int cnt=0;
            int flips = 0;
            while(i<j){
                if(grid[i][c] == grid[j][c]){
                    if(grid[i][c] == 1) cnt+=2;
                }else{
                    flips++;
                }
                i++;
                j--;
            }
            int r = m/2;
            i=0;
            j=n-1;
            while(i<j){
                if(grid[r][i] == grid[r][j]){
                    if(grid[r][i] == 1) cnt+=2;
                }else{
                    flips++;
                }
                i++;
                j--;
            }
            if(cnt%4 == 0){
                flip += flips;
            }else{
                if(flips >= 1){
                    flip += flips;
                }else{
                    flip += 2;
                }
            }
            if(grid[m/2][n/2] == 1) flip++;
        }
        return flip;
    }
};
