func minFlips(grid [][]int) int {
    res := 0
    for i := 0; i < len(grid) / 2; i++ {
        for j := 0; j < len(grid[i]) / 2; j++ {
            count := grid[i][j] + grid[len(grid)-i-1][j] + grid[i][len(grid[i])-j-1] + grid[len(grid)-i-1][len(grid[i])-j-1]
            if count == 2 {
                res += 2
            } else if count == 1 || count == 3 {
                res++
            }
        }
    }

    if (len(grid) % 2 == 1) && (len(grid[0]) % 2 == 1) {
        res += grid[(len(grid) + 1) / 2 - 1][(len(grid[0])+1) / 2 - 1]
    }

    pairsToFix := 0
    goodOnePairs := 0
    if len(grid[0]) % 2 == 1 {
        j := (len(grid[0])+1) / 2 - 1
        for i := 0; i < len(grid) / 2; i++ {
            //fmt.Println(i, j, len(grid)-i-1, j)
            summ := grid[i][j] + grid[len(grid)-i-1][j]
            if summ == 2 {
                goodOnePairs++
            }
            if summ == 1 {
                pairsToFix++
            }
        }
    }
    if len(grid) % 2 == 1 {
        i := (len(grid)+1) / 2 - 1
        for j := 0; j < len(grid[i]) / 2; j++ {
            summ := grid[i][j] + grid[i][len(grid[i])-j-1]
            if summ == 2 {
                goodOnePairs++
            }
            if summ == 1 {
                pairsToFix++
            }
        }
    }
    res += pairsToFix
    if pairsToFix == 0 && goodOnePairs % 2 == 1 {
        res += 2
    }
    return res
}
