class Solution:
    def minFlips(self, grid: List[List[int]]) -> int:
        n, m = len(grid), len(grid[0])
        mi = 0

        for i in range(n // 2):
            for j in range(m // 2):
                tl, tr, bl, br = grid[i][j], grid[i][m - j - 1], grid[n - i - 1][j], grid[n - i - 1][m - j - 1]
                arr = [tl, tr, bl, br]
                o = sum(arr)
                mi += min(o, 4 - o)

        e, b = 0, 0
        if n % 2 != 0:
            mid = n // 2
            for j in range(m // 2):
                if grid[mid][j] != grid[mid][m - j - 1]:
                    mi += 1
                    e += 1
                else:
                    if grid[mid][j] == 1:
                        b += 1

        if m % 2 != 0:
            mid = m // 2
            for i in range(n // 2):
                if grid[i][mid] != grid[n - i - 1][mid]:
                    mi += 1
                    e += 1
                else:
                    if grid[i][mid] == 1:
                        b += 1

        if n % 2 != 0 and m % 2 != 0 and grid[n // 2][m // 2] == 1:
            mi += 1

        if e == 0 and b % 2 != 0:
            mi += 2

        return mi
