class Solution:
    def minFlips(self, grid: List[List[int]]) -> int:
        m = len(grid)
        n = len(grid[0])
        cal_4 = 0
        for i in range(m//2):
            for j in range(n//2):
                tt = grid[i][j]+grid[i][n-j-1]+grid[m-i-1][j]+grid[m-i-1][n-j-1]
                if tt==0 or tt==4:
                    pass
                else:
                    cal_4 += min(tt,4-tt)
        ones = 0
        cal_2 = 0
        if m%2!=0:
            i = m//2
            for j in range(n//2):
                if  grid[i][j] != grid[i][n-j-1]:
                    cal_2 += 1

                elif grid[i][j] == 1:
                    ones += 2
            ones = ones % 4

        if n%2!=0:
            j = n//2
            for i in range(m//2):
                if  grid[i][j] != grid[m-i-1][j]:
                    cal_2 += 1

                elif grid[i][j] == 1:
                    ones += 2
            ones = ones % 4
        if n%2 == 1 and m%2 ==1:
            ones += grid[m//2][n//2]
            ones = ones % 4
        #print(ones, cal_4,cal_2)
        if cal_2==0:
            cal_2 += ones
        else:
            if ones==1 or ones ==3:
                cal_2+=1
        return cal_4+cal_2
