class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int n=grid.size(),m=grid[0].size();
        int ans=0;
        for(int i=0;i<n/2;i++)
        for(int j=0;j<m/2;j++) 
        {
            int cnt=0;
            if(grid[i][j]==1)
            cnt++;
            if(grid[i][m-1-j]==1)
            cnt++;
            if(grid[n-i-1][j]==1)
            cnt++;
            if(grid[n-i-1][m-1-j]==1)
            cnt++;
            ans+=min(cnt,4-cnt);
        }   
        int one=0,cnt=0;
        // cout<<ans<<"\n";
        if(n%2)
        {
            int row=n/2;
            int l=0,r=m-1;
            while(l<r)
            {
                if(grid[row][l]!=grid[row][r])
                {
                    cnt++;
                }
                else one+=grid[row][l];
                l++;
                r--;
            }

        }
        if(m%2)
        {
            int col=m/2;
            int l=0,r=n-1;
            while(l<r)
            {
                if(grid[l][col]!=grid[r][col])
                {
                    cnt++;
                }
                else one+=grid[l][col];   
                l++;
                r--;  
            }
        }
        if(cnt==0)
        {
            if(one%2==1)
            ans+=2;
        }
        else ans+=cnt;
        if(n%2&&m%2)
        {
            ans+=(grid[n/2][m/2]==1);
        }
        return ans;
    }
};
