class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int s=0,e=grid.size()-1;
        int flips=0;
        while(s<e){
            int sv=0,ev=grid[0].size()-1;
            while(sv<ev){
                int a=0;
                if(grid[s][sv])a++;
                if(grid[s][ev])a++;
                if(grid[e][sv])a++;
                if(grid[e][ev])a++;
                if(a==1||a==3)flips++;
                if(a==2)flips+=2;
                sv++;
                ev--;
            }
            s++;
            e--;
        }
        int ones=0;
        int fs=0;

        if(grid.size()%2){
            int in=grid.size()/2;
            s=0,e=grid[0].size()-1;
            while(s<e){
                if(grid[in][s]!=grid[in][e]){
                    flips++;
                    fs++;
                }else if(grid[in][s])ones+=2;
                s++;
                e--;
            }
        }
        if(grid[0].size()%2){
            int in=grid[0].size()/2;
            s=0,e=grid.size()-1;
            while(s<e){
                if(grid[s][in]!=grid[e][in]){
                    flips++;
                    fs++;
                }else if(grid[s][in])ones+=2;
                s++;
                e--;
            }
        }

        if(grid.size()%2&&grid[0].size()%2){
            if(grid[grid.size()/2][grid[0].size()/2])flips++;
        }
        if(ones%4){
            if(fs==0)flips+=2;
        }
        return flips;
    }
};
